from Vigenere import Vigenere

v = Vigenere()
temp = v.encrypt_text('',
                      'g', 'en')
print(f"Зашифрованный текст: {temp}")
te, k = v.decrypt_text(temp, 'en')
print(f"Расшифрованный текст: {k, te}")

